  private void jButtonSaludarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonSaludarActionPerformed
    if (this.jTextFieldNombre.getText().isEmpty()){
      JOptionPane.showMessageDialog(null, "Pon√© un nombre, tonto", "ERROR", JOptionPane.ERROR_MESSAGE);
    }else{
      String nombre = this.jTextFieldNombre.getText();
      String elSaludo = "Hola " + nombre;
      
      if (this.jCheckBoxEntusiasmo.isSelected()) {
        elSaludo += " !!!!!! Capo idolo te quiero";
      }
      JOptionPane.showMessageDialog(null, elSaludo, "SALUDANDO", JOptionPane.ERROR_MESSAGE);
    }
  }//GEN-LAST:event_jButtonSaludarActionPerformed

